app.controller("AnimationEditorController",["$rootScope","AnimationService",function(a,b){a.init=function(){a.page.title="Editor de Animações"};a.initAnimation=function(){b.init()};a.data={images:[],image:null};a.graphic={rows:1,cols:1,gridColor:"#000000"};a.animation={frames:[]};a.layers={canvas:[],width:560,height:400,visible:null};a.changeRows=function(c){a.graphic.rows=c;b.changeRows(c)};a.changeCols=function(c){a.graphic.cols=c;b.changeCols(c)};a.addImages=function(c){a.data.images=a.data.images.concat(c)};a.changeGraphic=function(){b.changeGraphic(a.data.image.url)};a.changeGridColor=function(c){a.graphic.gridColor=c;b.changeGridColor(a.graphic.gridColor)};a.addFrame=function(){a.layers.visible=a.layers.canvas.length;a.layers.canvas.push({})};a.removeFrame=function(c){a.layers.canvas.splice(c,1)};a.selectFrame=function(c){a.layers.visible=c}}]);